import { Component } from "react";
import "./App.css";

class App extends Component {
  constructor() {
    super();

    this.state = {
      monsters: [
        {
          name: 'Frankenstein'
        }, 
        {
          name: 'Dracula'
        }, 
        {
          name: 'Zombie'
        }
      ],
    }
  }

	render() {
		return (
			<div className="App">
				<header className="App-header">
					<p>{ this.state.string }</p>
					<button onClick={() => this.setState({ string: 'Hello Andrei' })}>Change Text</button>
				</header>
			</div>
		);
	}
}

export default App;
